import "./App.css";
import Galaxy from "./galaxy";

function App() {
  return (
    <div className="main">
      <div className="wrap-galaxy">
        <Galaxy></Galaxy>
      </div>
      <main>
        <div className="wrap-head">
          <div className="button"></div>
          <h2 className="description">
            Hi! This is Jayson. I am student, software & interface developer,
            artist, barista, and brain-science geek. Specifically, I find
            interest in exploring the intersection between machines, software,
            and human behaviour. Grew up in Toronto, studying Cognitive Science
            at UCLA. Email / Linkedin
          </h2>
        </div>
      </main>
    </div>
  );
}

if (typeof document !== `undefined`) {
  window.addEventListener("load", () => {
    const el = document.querySelector(".wrap-galaxy");
    const observer = new IntersectionObserver(
      ([e]) => e.target.classList.toggle("pinned", e.intersectionRatio < 40),
      { threshold: [40] }
    );
    observer.observe(el);
  });
}

export default App;
