import * as React from "react";
import { useRef } from "react";
import { motion, useInView } from "framer-motion";
import "./Projects.css";

import matcha from "./assets/matcha.png";
import mapify from "./assets/mapify.png";

function Projects() {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true });
  const list = {
    visible: {
      opacity: 1,
      transition: {
        when: "beforeChildren",
        staggerChildren: 0.2,
      },
    },
  };
  const item = {
    visible: { opacity: 1, y: 0 },
    hidden: { opacity: 0, y: 50 },
  };
  return (
    <React.Fragment>
      <div className="projects-wrapper">
        <section ref={ref}>
          <motion.h2
            style={{
              transform: isInView ? "none" : "translateX(-200px)",
              opacity: isInView ? 1 : 0,
              transition: "all 0.6s cubic-bezier(0.17, 0.55, 0.55, 1) 0.2s",
            }}
          >
            What I've Built
          </motion.h2>
          <motion.div
            className="grid"
            initial="hidden"
            animate={isInView ? "visible" : "hidden"}
            variants={list}
          >
            <motion.div
              variants={item}
              whileHover={{
                scale: 1.2,
                transition: { duration: 1 },
              }}
              className="card card-project col-span-2"
            >
              <div className="img-wrap">
                <img src={mapify}></img>
              </div>
              <div className="info-wrap">
                <h3>Mapify Case Study</h3>
                <p>
                  Hybrid tracking and management of calendar events and todo
                  list via the Google Calendar and Google Tasks APIs.
                </p>
              </div>
            </motion.div>
            <motion.div variants={item} className="card  col-span-2">
              <h3>Card</h3>
            </motion.div>
            <motion.div variants={item} className="card">
              <h3>Card</h3>
            </motion.div>
            <motion.div variants={item} className="card">
              <h3>Card</h3>
            </motion.div>
          </motion.div>
        </section>
      </div>
    </React.Fragment>
  );
}

export default Projects;
